<?php	$file    = fopen( $filename, 'r' );	$devices = [];	while ( ( $line = fgetcsv( $file ) ) !== FALSE ) {		//$line is an array of the csv elements		$line[ 1 ] = explode( "|", $line[ 1 ] );		$devices[] = $line;	}	fclose( $file );?><div class='center'>	<?php		if ( isset( $devices ) && !empty( $devices ) ):			foreach ( $devices as $device_group ): ?>				<?php foreach ( $device_group[ 1 ] as $key => $device ): ?>					<?php					if ( !isset( $device_group[ 4 ] ) || empty( $device_group[ 4 ] ) ) {						$device_group[ 4 ] = "bulb_1";					}					$img = _RESOURCESDIR_."img/device_icons/".$device_group[ 4 ]."_off.png";					?>					<div class='box_device'					     data-device_id='<?php echo $device_group[ 0 ]; ?>'					     data-device_group='<?php echo count( $device_group[ 1 ] ) > 1 ? "multi" : "single"; ?>'					     data-device_ip='<?php echo $device_group[ 2 ]; ?>'					     data-device_relais='<?php echo $key + 1; ?>'					>						<div class="animated rubberBand">							<img data-icon='<?php echo $device_group[ 4 ]; ?>' src='<?php echo $img; ?>' alt=''>						</div>												<div class='box_device_name'>							<?php echo $device; ?>						</div>					</div>				<?php				endforeach;			endforeach;		endif; ?></div><script type='text/javascript' src='<?php echo _RESOURCESDIR_; ?>js/start.js'></script>